import React, { useState } from 'react'
import { useHistory } from 'react-router-dom'
import './Search.css'
import SearchIcon from '@mui/icons-material/Search'
import FilterCenterFocusSharp from '@mui/icons-material/FilterCenterFocusSharp'
//import MicIcon from '@mui/icons-material/Mic'
import { Button } from '@mui/material'

function Search() {
    const [input, setInput] = useState("")
    const history = useHistory()

    const search = (e) => {
        e.preventDefault()
        console.log("You hit the search button")

        history.push('/search')
    }

    return (
      <form className="search">
        <div className="search__input">
          <SearchIcon className="search__inputIcon" />
          <input value={input} onChange={(e) => setInput(e.target.value)} />
          <FilterCenterFocusSharp />
        </div>

        <div className="search__buttons">
          <Button variant="outlined" type="submit" onClick={search}>Google Search</Button>
          <Button variant="outlined">I'm Feeling Lucky</Button>
        </div>
      </form>
    );
  }


export default Search
